hudson.tasks.BatchFile.command:
IF "%ExecutionMode%"=="TestDesign" (
SET RUN_PROP=-scenario "%Release_R_Scenario%" -testcase "%TestSet_R_TestCase%" -browser "%BrowserName%"
) ELSE (
SET RUN_PROP=-release "%Release_R_Scenario%" -testset "%TestSet_R_TestCase%" 
)
Run.bat -run -project_location "%ProjectLocation%" %RUN_PROP% %AdditionalArgs%
